import { Component, AfterViewInit, ElementRef, ViewChild, OnDestroy, Input } from '@angular/core';
import { Subscription } from 'rxjs';
import { Router } from '@angular/router';
import { OlympicService } from '../../core/services/olympic.service'; // Import du service
import { Chart } from 'chart.js/auto';

@Component({ // Création du component
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrl: './chart.component.scss'
})

export class ChartComponent implements AfterViewInit, OnDestroy {
  // Médaille par année
  @Input() participations!: { year: number; medalsCount: number }[]; 
  // Stocker l'abonnement
  private subscription: Subscription = new Subscription(); 

  // Utilisation de ViewChild plus performant que getElementById
  @ViewChild('myChart') myChart!: ElementRef<HTMLCanvasElement>; // Sélectionne l’élément HTML <canvas> avec l’ID #myChart

  constructor(private olympicService: OlympicService, private router: Router) {} // Ajout de l'injection

  private chart!: Chart; // Stocke l’objet Chart.js pour pouvoir le manipuler

  ngAfterViewInit() {
    this.olympicService.getOlympicData().subscribe(({ labels, data }) => {
      this.createChart(labels, data); // Passe les données au graphique
    });
  }

  // Chart home page
  createChart(labels: string[], values: number[]) {
    this.chart = new Chart(this.myChart.nativeElement, {
      type: 'pie',  // this.chartType as ChartType
      data: {
        labels: labels, // Utilisation des labels dynamiques
        datasets: [{
          label: 'Medal per country',
          data: values, // Utilisation des valeurs dynamiques
          borderWidth: 1,
          backgroundColor: ['red', 'blue', 'yellow', 'green', 'purple']
        }]
      },
      options: {
        layout: {
          padding: 20
        },
        responsive: true,
        plugins: {
          legend: {
            labels: {
              font: {
                size: Math.max(10, window.innerWidth * 0.018), // Adapte la font-size de la legende: responsiveness
                // onClick: (event) => this.onChartClick(event)
                onClick: (event, elements) => this.onChartClick(event, elements)
              }
            }
          }
        }
      }
    });
  }

  onChartClick(event: MouseEvent): void {
    const chart = (event.target as HTMLCanvasElement).getContext('2d')?.chart;
    const activePoints = chart?.getElementsAtEventForMode(event, 'nearest', { intersect: true }, false);
    debugger
    if (activePoints && activePoints.length > 0) {
      const countryIndex = activePoints[0].index;  // L'index du pays cliqué dans le graphique
      const countryId = this.countries[countryIndex].id;  // Récupérer l'ID du pays

      // Rediriger vers la page de détails du pays
      this.router.navigate([`/country/${countryId}`]);
    }
  }


  ngOnDestroy(): void {
    this.subscription.unsubscribe(); // Désabonnement propre
    console.log('ChartComponent détruit, abonnement nettoyé.');
  }
}
