import { Component, AfterViewInit, ElementRef, ViewChild, Input } from '@angular/core';
import { OlympicService } from '../../core/services/olympic.service'; // Import du service
// import { OlympicData } from '../../core/models/Olympic';
import { OlympicData } from '../../core/models/Olympic';

import Chart, { ChartType } from 'chart.js/auto';

@Component({ // Création du component
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrl: './chart.component.scss'
})

export class ChartComponent implements AfterViewInit {
  // Utilisation de ViewChild plus performant que getElementById
  @ViewChild('myChart') myChart!: ElementRef<HTMLCanvasElement>; // Sélectionne l’élément HTML <canvas> avec l’ID #myChart

  @Input() chartType: string = 'pie'; // Type de graphique (pie, line, pie...)
  @Input() chartData: any; // Données dynamiques
  @Input() chartOptions: any; // Options dynamiques

  constructor(private olympicService: OlympicService) {} // Ajout de l'injection

  chart!: Chart; // Stocke l’objet Chart.js pour pouvoir le manipuler

  ngAfterViewInit() {
    // this.olympicService.getChartData().subscribe(data => {
    //   this.createChart(data.labels, data.data);
    // });
    this.olympicService.getOlympicCountries().subscribe((countries: string[]) => {
      const values = countries.map(() => Math.floor(Math.random() * 100)); // Simule des valeurs aléatoires

      this.createChart(countries, values); // ✅ Passe les pays comme labels
    });
  }

  createChart(labels: string[], values: number[]) {
    console.log("data------------------------");
    console.log("data------------------------");
    console.log(this)
    console.log("===============================================")
    console.log("===============================================")
    console.log("===============================================")
    console.log(this.chart)
    console.log("===============================================")
    console.log("===============================================")
    console.log(this.chartData)
    console.log(this.olympicService)
    console.log("===============================================")
    console.log(this.olympicService)
    console.log("data------------------------")
    console.log("data------------------------")
    this.chart = new Chart(this.myChart.nativeElement, {
      type: 'pie',  // this.chartType as ChartType
      data: {
        labels: labels, // Utilisation des labels dynamiques
        datasets: [{
          label: 'Medal per country',
          data: values, // Utilisation des valeurs dynamiques
          borderWidth: 1,
          backgroundColor: ['red', 'blue', 'yellow', 'green', 'purple']
        }]
      },
      options: {
        responsive: true,
        scales: {
          y: { beginAtZero: true }
        }
      }
    });
  }
}
