import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { Observable, Subscription, take } from 'rxjs';
import { Olympic } from 'src/app/core/models/Olympic';
import { OlympicService } from 'src/app/core/services/olympic.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})

export class HomeComponent implements OnInit, OnDestroy {

  private subscription: Subscription = new Subscription(); // Stocker l'abonnement

  public olympics$!: Observable<Olympic[]>;
  public maxParticipations!: number;
  public allCountries!: number;

  constructor(private olympicService: OlympicService, private router: Router) {}

  ngOnInit(): void {
    this.olympics$ = this.olympicService.getOlympics();

    // .pipe(take(1): utiliser take(1) dans tes abonnements pour éviter d'accumuler des abonnements inutiles
    this.subscription.add(
      this.olympicService.getNumberOfParticipations().pipe(take(1)).subscribe((maxParticipations: number) => {
        this.maxParticipations = maxParticipations;
        console.log(`Max participations : ${maxParticipations}`);
      })
    );

    this.subscription.add(
      this.olympicService.getNumBerOfCountries().pipe(take(1)).subscribe((allCountries: number) => {
        this.allCountries = allCountries;
        console.log(`All Countries: ${allCountries}`)
      })
    );
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe(); // Désabonnement
    console.log('HomeComponent détruit, abonnement nettoyé.');
  }
}
