import { Component, OnInit } from '@angular/core';
import { Observable, of } from 'rxjs';
import { Olympic } from 'src/app/core/models/Olympic';
import { OlympicService } from 'src/app/core/services/olympic.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  public olympics$!: Observable<Olympic[]>;

  constructor(private olympicService: OlympicService) {}

  ngOnInit(): void {
    this.olympics$ = this.olympicService.getOlympics().subscribe(({ id, country, medalsCount, participation })
     => {
     }
  }
}


  ngAfterViewInit() {
    this.olympicService.getOlympicData().subscribe(({ labels, data }) => {
      this.createChart(labels, data); // Passe les données au graphique
    });
  }
