import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';
import { Olympic } from '../models/Olympic';
import { Observable, map } from 'rxjs';

@Injectable({
  providedIn: 'root',
})

export class OlympicService {
  private olympicUrl = './assets/mock/olympic.json';
  private olympics$ = new BehaviorSubject<Olympic[]>([]);

  constructor(private http: HttpClient) {}

  loadInitialData() {
    return this.http.get<Olympic[]>(this.olympicUrl).pipe(
      tap((value) => this.olympics$.next(value)),
      catchError((error, caught) => {
        // TODO: improve error handling
        console.error(error);
        // can be useful to end loading state and let the user know something went wrong
        this.olympics$.next([]);
        return caught;
      })
    );
  }

  getOlympicData(): Observable<{ labels: string[], data: number[] }> {
    return this.http.get<Olympic[]>(this.olympicUrl).pipe(
      map((olympics: Olympic[]) => {
        const labels = olympics.map(o => o.country); // Pays comme labels
        const data = olympics.map(o =>
          o.participations.reduce((sum, p) => sum + p.medalsCount, 0) // Somme des médailles
        );

        return { labels, data }; // Format adapté pour Chart.js
      })
    );
  }

  getOlympics() { // typer l'observable -> done
    return this.olympics$.asObservable().pipe(
      map((olympics: Olympic[]) =>
        olympics.map(olympic => ({
          id: olympic.id,
          country: olympic.country,
          medalsCount: olympic.participations.reduce((sum, p) => sum + p.medalsCount, 0), // Calcul du total des médailles
          participations: olympic.participations // Conserve la liste des participations
        }))
      )
    );
  }

  getNumberOfParticipations(): Observable<number> {
    return this.olympics$.pipe(
      map((data: Olympic[]) => {
        if (data && data.length > 0) {
          return Math.max(...data.map(olympic => olympic.participations.length)); // Trouver le max, spread operator (...) transforme le tableau en liste de valeurs pour Math.max()
        }
        return 0; // Si aucune donnée, on retourne 0
      })
    );
  }

  getNumBerOfCountries() {
    return this.olympics$.pipe(
      map((data: Olympic[]) => {
        return data.map(olympic => olympic.country).length;
      })
    );
  }

  getNumberOfAtheles() {
    return this.olympics$.pipe(
      map((data: Olympic[]) => {
        // return data.map(olympic => olympic.participations.reduce((sum, p) => sum + p.medalsCount, 0));
        return data.map(olympic => olympic.country).length;
        debugger
      })
    );
  }


