import { Component, OnInit, OnDestroy } from '@angular/core';
import { Observable, Subscription, map, of, tap } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';
import { Location } from '@angular/common';
import { Olympic } from 'src/app/core/models/Olympic';
import { OlympicService } from 'src/app/core/services/olympic.service';
import { catchError} from 'rxjs/operators';
import { ChartData, ChartOptions } from 'chart.js';

@Component({
  selector: 'app-country-details',
  templateUrl: './country-details.component.html',
  styleUrl: './country-details.component.scss'
})

export class CountryDetailsComponent implements OnInit{
  public olympics$!: Observable<Olympic[]>;
  public countryId!: number | null;
  public countryDetails$!: Observable<Olympic | null>;
  public numberOfEntries!: number; // pour tous les pays
  public numberOfAthletes$!: Observable<number>;
  public numberOfMedals$!: Observable<number>;
  public numberOfEntriesForCountry$!: Observable<number>; // pour un seul pays

  // Configuration des donn√©es pour le Line Chart
  public lineChartData: ChartData<'line'> = {
    labels: [], // Ann√©es
    datasets: [
      {
        data: [], // Nombre de m√©dailles
        label: 'M√©dailles',
        borderColor: '#42A5F5',
        backgroundColor: 'rgba(66,165,245,0.2)',
        fill: true,
      },
    ],
  };

  // Options du graphique
  public lineChartOptions: ChartOptions<'line'> = {
    responsive: true,
  };

  // Stocker l'abonnement
  private subscription: Subscription = new Subscription(); 

  constructor(
    private olympicService: OlympicService, 
    private route: ActivatedRoute, 
    private router: Router,
    private location: Location // Ajout de Location pour revenir √† la page pr√©c√©dente pas forc√©ment √† home
  ) {}

ngOnInit(): void {
  // R√©cup√®re l'ID depuis l'URL
  this.countryId = Number(this.route.snapshot.params['id']);
  console.log("üåç ID r√©cup√©r√© depuis l'URL :", this.countryId);

  if (isNaN(this.countryId)) {
    console.warn("‚õî ID invalide, redirection...");
    this.router.navigate(['/404']);
    return;
  }

  // V√©rifier si getOlympicById fonctionne bien
  this.olympicService.getOlympicById(this.countryId).subscribe(country => {
    console.log("üîç R√©sultat de getOlympicById :", country);
  });

  // Charger les d√©tails du pays
  this.countryDetails$ = this.olympicService.getOlympicById(this.countryId).pipe(
    tap(country => console.log("üîç Pays trouv√© :", country)),
    map(country => {
      if (!country) {
        console.warn("‚õî Aucun pays trouv√©, redirection...");
        this.router.navigate(['/404']);
        return null;
      }
      return country;
    }),
    catchError(error => {
      console.error("‚ùå Erreur lors de la r√©cup√©ration du pays :", error);
      this.router.navigate(['/404']);
      return of(null);
    })
  );

  // Souscription pour voir les logs
  this.countryDetails$.subscribe();
}


  // Revient √† la page pr√©c√©dente
  goBack(): void {
    this.location.back(); 
  }

  // D√©sabonnement
  ngOnDestroy(): void {
    this.subscription.unsubscribe(); 
    console.log('CountryDetailsComponent d√©truit, abonnement nettoy√©.');
  }
}
