  <button (click)="goBack()" class="bg-blue-500 text-white px-4 py-2 rounded-lg mt-4 hover:bg-blue-700">
    ‚¨Ö Back
  </button>
  <div *ngIf=" countryDetails$ | async as country " class="bg-black bg-opacity-50 p-8 rounded-lg max-w-3xl w-full text-center flex flex-col items-center">
    <h1 class="text-2xl font-bold"> {{country.country}}'s Details</h1>
    <div *ngIf=" numberOfEntriesForCountry$ | async as nbEntries ">
      <p>Number of Entries: {{ nbEntries }}</p>
    </div>
    <div *ngIf=" numberOfAthletes$ | async as nbAthlete ">
      <p>Total number of athletes: {{ nbAthlete }}</p>
    </div>
    <div *ngIf=" numberOfMedals$ | async as nbMedals ">
      <p>Total number of medals: {{ nbMedals }}</p>
    </div>
    <p></p>
  </div>

</section>
