import { Component, OnInit, OnDestroy, input } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';
import { Olympic } from 'src/app/core/models/Olympic';
import { OlympicService } from 'src/app/core/services/olympic.service';

@Component({
  selector: 'app-country-details',
  templateUrl: './country-details.component.html',
  styleUrl: './country-details.component.scss'
})

export class CountryDetailsComponent implements OnInit{
  public olympics$!: Observable<Olympic[]>;
  public countryId!: number;
  public countryDetails$!: Observable<Olympic | undefined>;
  public numberOfEntries!: number;
  public numberOfAthletes!: number;

  // Stocker l'abonnement
  private subscription: Subscription = new Subscription(); 

  constructor(private olympicService: OlympicService, private route: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    this.olympicService.getNumberOfParticipations().subscribe((numberOfEntries: number) => {
      this.numberOfEntries = numberOfEntries;
      console.log(`Nb de participations : ${numberOfEntries}`);
    });

    // Récupère l'id via la route et le transforme en number
    this.countryId = Number(this.route.snapshot.params['id']); 
    // Charger les détails du pays
    this.countryDetails$! = this.olympicService.getOlympicById(this.countryId);
    console.log(this.countryDetails$!)
  }

  // this.olympicService.getNumberOfAtheles().subscribe((numberOfAthletes: number) => {
  //   this.numberOfAthletes = numberOfAthletes;
  //   console.log(`Nb de numberOfAthletes : ${numberOfAthletes}`);
  // });

  ngOnDestroy(): void {
    this.subscription.unsubscribe(); // Désabonnement
    console.log('CountryDetailsComponent détruit, abonnement nettoyé.');
  }
}
<section class="relative text-white h-screen flex items-center justify-center bg-cover bg-center col-sm-12">
  <div class="bg-black bg-opacity-50 p-8 rounded-lg max-w-3xl w-full text-center flex flex-col items-center">
    <h1 class="text-2xl font-bold">Medals per Countries</h1>
    <div>
      {{ countryDetails$ | async }}
      <p>Country: {{ country.country }}</p>
    </div>
    <p>Number of Entries: {{ numberOfEntries }}</p>
    <p>Number of Athletes: {{ numberOfAthletes }}</p>
  </div>
</section>
